mapper.select:
select d.id_, d.enable_, d.remark_, d.create_by, d.create_time, d.update_by, d.update_time, d.version_, d.name_, d.english_name, d.port_, d.description_, d.sort_no, d.station_group_id, s.name_ as station_group_name from resource_domain d join resource_station_group s on s.enable_ != -1 and d.station_group_id = s.id_ where d.enable_ != -1
and d.station_group_id = #{domainVo.stationGroupId}
and (d.name_ like concat('%',#{domainVo.name},'%') or d.english_name like concat('%',#{domainVo.name},'%'))

select d.id_, d.enable_, d.remark_, d.create_by, d.create_time, d.update_by, d.update_time, d.version_, d.name_, d.english_name, d.port_, d.description_, d.sort_no, d.station_group_id, s.name_ as station_group_name from resource_domain d join resource_station_group s on s.enable_ != -1 and d.station_group_id = s.id_ where d.enable_ != -1
and d.station_group_id = #{domainVo.stationGroupId}
and (d.name_ like concat('%',#{domainVo.name},'%') or d.english_name like concat('%',#{domainVo.name},'%'))

select count(*) from resource_domain
where enable_ != -1 and name_ = #{name}

    and id_ != #{id}

select count(*) from resource_domain
where enable_ != -1 and english_name = #{englishName}

    and id_ != #{id}
