mapper.select:
select s.id_, s.enable_, s.remark_, s.create_by, s.create_time, s.update_by, s.update_time, s.version_, s.name_, s.english_name, s.abbreviation_, s.description_, s.sort_no, s.station_group_classification_id, c.name_ as station_group_classification_name, c.tree_position as station_group_classification_tree_position, s.department_id, d.name_ as department_name, d.tree_position as department_tree_position, (select count(*) from resource_station_group_user rsgu where rsgu.station_group_id = s.id_) as userNumber from resource_station_group s join resource_station_group_classification c on c.enable_ = 1 and s.station_group_classification_id = c.id_ join admin_department d on d.enable_ = 1 and s.department_id = d.id_ where s.enable_ != -1
and s.station_group_classification_id = #{stationGroupVo.stationGroupClassificationId}
and s.department_id = #{stationGroupVo.departmentId}
and ( s.name_ like concat('%',#{stationGroupVo.name},'%') or s.english_name like concat('%',#{stationGroupVo.name},'%') or s.abbreviation_ like concat('%',#{stationGroupVo.name},'%') )
order by s.sort_no asc, s.update_time desc

select s.id_, s.enable_, s.remark_, s.create_by, s.create_time, s.update_by, s.update_time, s.version_, s.name_, s.english_name, s.abbreviation_, s.description_, s.sort_no, s.station_group_classification_id, c.name_ as station_group_classification_name, c.tree_position as station_group_classification_tree_position, s.department_id, d.name_ as department_name, d.tree_position as department_tree_position from resource_station_group s join resource_station_group_classification c on c.enable_ = 1 and s.station_group_classification_id = c.id_ join admin_department d on d.enable_ = 1 and s.department_id = d.id_ where s.enable_ != -1
and s.station_group_classification_id = #{stationGroupVo.stationGroupClassificationId}
and s.department_id = #{stationGroupVo.departmentId}
and ( s.name_ like concat('%',#{stationGroupVo.name},'%') or s.english_name like concat('%',#{stationGroupVo.name},'%') or s.abbreviation_ like concat('%',#{stationGroupVo.name},'%') )
order by s.sort_no asc, s.update_time desc

select count(*) from resource_station_group
where enable_ != -1 and name_ = #{name}

    and id_ != #{id}

select count(*) from resource_station_group
where enable_ != -1 and english_name = #{englishName}

    and id_ != #{id}

select count(*) from resource_station_group
where enable_ != -1 and abbreviation_ = #{abbreviation}

    and id_ != #{id}
